import React, { useState } from 'react';
import axios from 'axios';
import styles from './diabetes.css';

const UserAccount = () => {
    const [input, setInput] = useState({
        pregnancies: 5,
        glucose: 166,
        bloodPressure: 72,
        skinThickness: 19,
        insulin: 175,
        bmi: 25.8,
        diabetesPedigreeFunction: 0.587,
        age: 51
    });
    const [prediction, setPrediction] = useState(null);
    const [clicked, setClicked] = useState(false);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setInput(prevInput => ({
            ...prevInput,
            [name]: parseFloat(value) || 0
        }));
    };

    const handlePredict = async () => {
        try {
            const response = await axios.post('http://localhost:5000/predict', {
                input: Object.values(input)
            });
            setPrediction(response.data.prediction);
        } catch (error) {
            console.error('Error making prediction:', error);
        }
    };

    const handleClick = () => {
        setClicked(!clicked);
    };

    return (
        <>
            <nav className={styles.navbar}>
                <a href="#" className={styles.logo}>
                    <svg 
                        id="logo-15" 
                        width="49" 
                        height="48" 
                        viewBox="0 0 49 48" 
                        fill="none" 
                        xmlns="http://www.w3.org/2000/svg"
                    >
                        <path 
                            d="M24.5 12.75C24.5 18.9632 19.4632 24 13.25 24H2V12.75C2 6.53679 7.03679 1.5 13.25 1.5C19.4632 1.5 24.5 6.53679 24.5 12.75Z" 
                            className="ccustom" 
                            fill="#17CF97"
                        />
                        <path 
                            d="M24.5 35.25C24.5 29.0368 29.5368 24 35.75 24H47V35.25C47 41.4632 41.9632 46.5 35.75 46.5C29.5368 46.5 24.5 41.4632 24.5 35.25Z" 
                            className="ccustom" 
                            fill="#17CF97"
                        />
                        <path 
                            d="M2 35.25C2 41.4632 7.03679 46.5 13.25 46.5H24.5V35.25C24.5 29.0368 19.4632 24 13.25 24C7.03679 24 2 29.0368 2 35.25Z" 
                            className="ccustom" 
                            fill="#17CF97"
                        />
                        <path 
                            d="M47 12.75C47 6.53679 41.9632 1.5 35.75 1.5H24.5V12.75C24.5 18.9632 29.5368 24 35.75 24C41.9632 24 47 18.9632 47 12.75Z" 
                            className="ccustom" 
                            fill="#17CF97"
                        />
                    </svg>
                </a>
                <div>
                    <ul id="navbar" className={clicked ? styles.active : ''}>
                        <li><a className={styles.active} href="/admin">Back</a></li>
                        <li><a href="/home">Logout</a></li>
                    </ul>
                </div>
                <div id="mobile" onClick={handleClick}>
                    <i id="bar" className={clicked ? 'fas fa-times' : 'fas fa-bars'}></i>
                </div>
            </nav>
            <div className={styles.container}>
                <h1 className={styles.title}>Diabetes Prediction</h1>
                <div className={styles.form}>
                    {Object.keys(input).map(key => (
                        <div className={styles.formGroup} key={key}>
                            <label className={styles.label}>
                                {key.replace(/([A-Z])/g, ' $1').toUpperCase()}:
                                <input
                                    type="number"
                                    step={key === 'bmi' || key === 'diabetesPedigreeFunction' ? '0.1' : '1'}
                                    name={key}
                                    value={input[key]}
                                    onChange={handleChange}
                                    className={styles.input}
                                />
                            </label>
                        </div>
                    ))}
                    <button onClick={handlePredict} className={styles.button}>Predict</button>
                </div>
                {prediction !== null && (
                    <div className={styles.result}>
                        <h2>Prediction Result:</h2>
                        {prediction === 0 ? 'The person is not diabetic' : 'The person is diabetic'}
                    </div>
                )}
            </div>
        </>
    );
};

export default UserAccount;
