import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import * as Components from './Components';
import axios from 'axios';
import User from './User';

function Auth() {
    const [signIn, toggle] = useState(true);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [name, setName] = useState('');
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const [user, setUser] = useState(null); // Store user details after successful login/signup
    const navigate = useNavigate();

    const handleSignUp = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.post('/api/auth/register', { name, email, password });
            if (response.status === 201) {
                alert('Account created successfully!');
                setUser(response.data.user); // Assuming the backend returns the user details
                navigate('/account'); // Redirect to the account page
            } else {
                setError(response.data.message || 'Failed to create account');
            }
        } catch (err) {
            setError(err.response?.data?.message || 'Something went wrong. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    const handleSignIn = async () => {
        setLoading(true);
        setError('');
        try {
            const response = await axios.post('/api/auth/login', { email, password });
            if (response.data.token) {
                alert('Sign In successful!');
                setUser(response.data.user); // Assuming the backend returns the user details
                navigate('/account'); // Redirect to the account page
            } else {
                setError('Invalid email or password');
            }
        } catch (err) {
            setError('Something went wrong. Please try again later.');
        } finally {
            setLoading(false);
        }
    };

    if (user) {
        return <User user={user} />; // Render the User component if the user is logged in
    }

    return (
        <Components.Container>
            <Components.SignUpContainer signinIn={signIn}>
                <Components.Form>
                    <Components.Title>Create Account</Components.Title>
                    <Components.Input
                        type='text'
                        placeholder='Name'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                    />
                    <Components.Input
                        type='email'
                        placeholder='Email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Components.Input
                        type='password'
                        placeholder='Password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    {error && <Components.Paragraph style={{ color: 'red' }}>{error}</Components.Paragraph>}
                    <Components.Button onClick={handleSignUp} disabled={loading}>
                        {loading ? 'Creating Account...' : 'Sign Up'}
                    </Components.Button>
                </Components.Form>
            </Components.SignUpContainer>

            <Components.SignInContainer signinIn={signIn}>
                <Components.Form>
                    <Components.Title>Sign In</Components.Title>
                    <Components.Input
                        type='email'
                        placeholder='Email'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                    />
                    <Components.Input
                        type='password'
                        placeholder='Password'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                    />
                    {error && <Components.Paragraph style={{ color: 'red' }}>{error}</Components.Paragraph>}
                    <Components.Anchor href='#'>Forgot your password?</Components.Anchor>
                    <Components.Button onClick={handleSignIn} disabled={loading}>
                        {loading ? 'Signing In...' : 'Sign In'}
                    </Components.Button>
                </Components.Form>
            </Components.SignInContainer>

            <Components.OverlayContainer signinIn={signIn}>
                <Components.Overlay signinIn={signIn}>
                    <Components.LeftOverlayPanel signinIn={signIn}>
                        <Components.Title>Welcome Back!</Components.Title>
                        <Components.Paragraph>
                            To keep connected with us please login with your personal info
                        </Components.Paragraph>
                        <Components.GhostButton onClick={() => toggle(true)}>
                            Sign In
                        </Components.GhostButton>
                    </Components.LeftOverlayPanel>

                    <Components.RightOverlayPanel signinIn={signIn}>
                        <Components.Title>Hello, Friend!</Components.Title>
                        <Components.Paragraph>
                            Enter Your personal details and start journey with us
                        </Components.Paragraph>
                        <Components.GhostButton onClick={() => toggle(false)}>
                            Sign Up
                        </Components.GhostButton>
                    </Components.RightOverlayPanel>
                </Components.Overlay>
            </Components.OverlayContainer>
        </Components.Container>
    );
}

function App() {
    return (
        <Router>
            <Routes>
                <Route path="/" element={<Auth />} />
                <Route path="/account" element={<User />} /> {/* Ensure this route is set up */}
            </Routes>
        </Router>
    );
}

export default App;
