import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
  Container, Grid, TextField, Button, Typography, Paper, CircularProgress, Box
} from '@mui/material';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import ErrorIcon from '@mui/icons-material/Error';
import './app.css'; // Custom styling

function HeartDisease() {
  const [inputData, setInputData] = useState({
    Age: '',
    Sex: '',
    ChestPainType: '',
    RestingBP: '',
    Cholesterol: '',
    FastingBS: '',
    RestingECG: '',
    MaxHR: '',
    ExerciseAngina: '',
    Oldpeak: '',
    ST_Slope: ''
  });
  const [result, setResult] = useState('');
  const [loading, setLoading] = useState(false);
  const [clicked, setClicked] = useState(false);
  const [history, setHistory] = useState([]);

  const handleChange = (event) => {
    setInputData({ ...inputData, [event.target.id]: event.target.value });
  };

  const handleSubmit = () => {
    setLoading(true);
    axios.post('http://localhost:5000/predict-heart-disease', inputData)
      .then(response => {
        setResult(response.data.result);
        setLoading(false);
        fetchHistory();
      })
      .catch(error => {
        console.error('Error predicting:', error);
        setLoading(false);
      });
  };

  const fetchHistory = () => {
    axios.get('http://localhost:5000/history-heart-disease')
      .then(response => {
        setHistory(response.data);
      })
      .catch(error => {
        console.error('Error fetching history:', error);
      });
  };

  useEffect(() => {
    fetchHistory();
  }, []);

  const handleClick = () => {
    setClicked(!clicked);
  };

  return (
    <>
      <nav>
        <a href="#">
          <svg 
            id="logo-15" 
            width="49" 
            height="48" 
            viewBox="0 0 49 48" 
            fill="none" 
            xmlns="http://www.w3.org/2000/svg"
          >
            <path 
              d="M24.5 12.75C24.5 18.9632 19.4632 24 13.25 24H2V12.75C2 6.53679 7.03679 1.5 13.25 1.5C19.4632 1.5 24.5 6.53679 24.5 12.75Z" 
              className="ccustom" 
              fill="#17CF97"
            />
            <path 
              d="M24.5 35.25C24.5 29.0368 29.5368 24 35.75 24H47V35.25C47 41.4632 41.9632 46.5 35.75 46.5C29.5368 46.5 24.5 41.4632 24.5 35.25Z" 
              className="ccustom" 
              fill="#17CF97"
            />
            <path 
              d="M2 35.25C2 41.4632 7.03679 46.5 13.25 46.5H24.5V35.25C24.5 29.0368 19.4632 24 13.25 24C7.03679 24 2 29.0368 2 35.25Z" 
              className="ccustom" 
              fill="#17CF97"
            />
            <path 
              d="M47 12.75C47 6.53679 41.9632 1.5 35.75 1.5H24.5V12.75C24.5 18.9632 29.5368 24 35.75 24C41.9632 24 47 18.9632 47 12.75Z" 
              className="ccustom" 
              fill="#17CF97"
            />
          </svg>
        </a>
        <div>
          <ul id="navbar" className={clicked ? "active" : ""}>
          <li><a className="active" href="/#">Back</a></li>
            <li><a className="active" href="/userAcc">Back</a></li>
            <li><a href="/home">Logout</a></li>
          </ul>
        </div>
        <div id="mobile" onClick={handleClick}>
          <i id="bar" className={clicked ? 'fas fa-times' : 'fas fa-bars'}></i>
        </div>
      </nav>
      <Container maxWidth="md">
        <Paper elevation={5} className="form-container">
          <Typography variant="h3" align="center" gutterBottom className="title">
            Heart Disease Prediction
          </Typography>

          <form>
            <Grid container spacing={3}>
              {Object.keys(inputData).map((key) => (
                <Grid item xs={12} sm={6} key={key}>
                  <TextField
                    id={key}
                    label={key.replace(/([A-Z])/g, ' $1')}
                    type="number"
                    variant="outlined"
                    fullWidth
                    value={inputData[key]}
                    onChange={handleChange}
                    InputLabelProps={{ shrink: true }}
                    className="input-field"
                  />
                </Grid>
              ))}
            </Grid>

            <Box 
              display="flex" 
              justifyContent="center" 
              alignItems="center" 
              marginTop="20px"
            >
              <Button
                variant="contained"
                color="primary"
                onClick={handleSubmit}
                disabled={loading}
                className="submit-button"
              >
                {loading ? <CircularProgress size={24} /> : 'Predict'}
              </Button>
            </Box>
          </form>

          {result && (
            <Paper elevation={3} className="result-box">
              {result === '0' ? (
                <>
                  <CheckCircleIcon color="success" style={{ fontSize: 50 }} />
                  <Typography variant="h6" className="result-text success-text">
                    No heart disease detected
                  </Typography>
                </>
              ) : (
                <>
                  <ErrorIcon color="error" style={{ fontSize: 50 }} />
                  <Typography variant="h6" className="result-text error-text">
                    Heart disease detected
                  </Typography>
                </>
              )}
            </Paper>
          )}
        </Paper>
      </Container>
    </>
  );
}

export default HeartDisease;
