// Home.js

import React, { Component } from "react";
import "./NavbarStyles.css"; // Updated CSS file for professional styling
//import logo from '../../Assets/logo.svg'; // Ensure the path to your logo is correct

class Home extends Component {
  state = { clicked: false };

  handleClick = () => {
    this.setState({ clicked: !this.state.clicked });
  };

  render() {
    return (
      <>
        <header className="header">
          <nav className="navbar">
            <a href="#" className="logo">
              {/* <img src={logo} alt="Health Predictor Logo" /> */}
            </a>
            <ul className={this.state.clicked ? "nav-links active" : "nav-links"}>
              <li><a href="/home" className="active">Home</a></li>
              <li><a href="/service">Service</a></li>
              <li><a href="/AboutUs">About Us</a></li>
              <li><a href="/login">Login</a></li>
            </ul>
            <div className="mobile-menu-icon" onClick={this.handleClick}>
              <i className={this.state.clicked ? 'fas fa-times' : 'fas fa-bars'}></i>
            </div>
          </nav>
          <div className="hero-section">
            <h1>Predict Heart Disease and Diabetes Risks Effortlessly</h1>
            <p>Our advanced prediction system helps you stay ahead of your health by providing accurate assessments of heart disease and diabetes risks based on your health data.</p>
            <button className="cta-button">Get Started</button>
          </div>
        </header>
        <section className="features">
          <h2>Why Choose Our Prediction System?</h2>
          <div className="features-grid">
            <div className="feature-item">
              <h3>Early Detection</h3>
              <p>Identify potential health risks early and take preventive measures to maintain your health.</p>
            </div>
            <div className="feature-item">
              <h3>Easy to Use</h3>
              <p>A simple, user-friendly interface designed to provide clear and actionable insights.</p>
            </div>
            <div className="feature-item">
              <h3>Accurate Predictions</h3>
              <p>Leverage state-of-the-art machine learning models for reliable health risk assessments.</p>
            </div>
          </div>
        </section>
      </>
    );
  }
}

export default Home;
