import React, { useState } from 'react';
import axios from 'axios';
import styles from "./diabetes.module.css";
import { Container, TextField, Button, Typography } from '@material-ui/core';

function UserAccount() {
  const [inputData, setInputData] = useState({
    Pregnancies: '',
    Glucose: '',
    BloodPressure: '',
    SkinThickness: '',
    Insulin: '',
    BMI: '',
    DiabetesPedigreeFunction: '',
    Age: ''
  });
  const [result, setResult] = useState('');

  const handleChange = (event) => {
    setInputData({ ...inputData, [event.target.id]: event.target.value });
  };

  const handleSubmit = () => {
    axios.post('http://localhost:5000/predict', inputData)
      .then(response => {
        setResult(response.data.result);
      })
      .catch(error => {
        console.error('Error predicting:', error);
      });
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" gutterBottom>Diabetes Prediction</Typography>
      <form>
        {Object.keys(inputData).map((key) => (
          <TextField
            key={key}
            id={key}
            label={key}
            type="number"
            variant="outlined"
            fullWidth
            margin="normal"
            value={inputData[key]}
            onChange={handleChange}
          />
        ))}
        <Button variant="contained" color="primary" onClick={handleSubmit}>
          Predict
        </Button>
      </form>
      {result && (
        <Typography variant="h6" style={{ marginTop: '20px' }}>
          {result === '0' ? 'The person is not diabetic' : 'The person is diabetic'}
        </Typography>
      )}
    </Container>
  );
}

export default UserAccount;
