import React, { useState } from "react";
import * as Components from './Components';
// import axios from 'axios';

function App() {
    const [signIn, toggle] = useState(true);
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [message, setMessage] = useState('');

    const handleSignUp = () => {
        axios.post('/api/signup', { name, email, password })
            .then(response => {
                setMessage(response.data.message);
                // Handle success or redirect
            })
            .catch(error => {
                if (error.response && error.response.status === 409) {
                    setMessage('User already exists');
                } else {
                    console.error('Error:', error);
                }
            });
    };

    const handleSignIn = () => {
        axios.post('/api/signin', { email, password })
            .then(response => {
                setMessage(response.data.message);
                // Handle success or redirect
            })
            // .catch(error => {
            //     if (error.response) {
            //         if (error.response.status === 401) {
            //             setMessage('Invalid password');
            //         } else if (error.response.status === 404) {
            //             setMessage('User not found');
            //         } else {
            //             console.error('Error:', error);
            //         }
            //     }
            // });
    };

    return (
        <Components.Container>
            {message && <p>{message}</p>}
            {signIn ? (
                <Components.SignUpContainer signinIn={signIn}>
                    <Components.Form>
                        <Components.Title>Create Account</Components.Title>
                        <Components.Input type='text' placeholder='Name' onChange={e => setName(e.target.value)} />
                        <Components.Input type='email' placeholder='Email' onChange={e => setEmail(e.target.value)} />
                        <Components.Input type='password' placeholder='Password' onChange={e => setPassword(e.target.value)} />
                        <Components.Button onClick={handleSignUp}>Sign Up</Components.Button>
                    </Components.Form>
                </Components.SignUpContainer>
            ) : (
                <Components.SignInContainer signinIn={signIn}>
                    <Components.Form>
                        <Components.Title>Sign in</Components.Title>
                        <Components.Input type='email' placeholder='Email' onChange={e => setEmail(e.target.value)} />
                        <Components.Input type='password' placeholder='Password' onChange={e => setPassword(e.target.value)} />
                        <Components.Anchor href='#'>Forgot your password?</Components.Anchor>
                        <Components.Button onClick={handleSignIn}>Sign In</Components.Button>
                    </Components.Form>
                </Components.SignInContainer>
            )}

            <Components.OverlayContainer signinIn={signIn}>
                <Components.Overlay signinIn={signIn}>
                    <Components.LeftOverlayPanel signinIn={signIn}>
                        <Components.Title>Welcome Back!</Components.Title>
                        <Components.Paragraph>
                            To keep connected with us please login with your personal info
                        </Components.Paragraph>
                        <Components.GhostButton onClick={() => toggle(true)}>
                            Sign In
                        </Components.GhostButton>
                    </Components.LeftOverlayPanel>

                    <Components.RightOverlayPanel signinIn={signIn}>
                        <Components.Title>Hello, Friend!</Components.Title>
                        <Components.Paragraph>
                            Enter Your personal details and start journey with us
                        </Components.Paragraph>
                        <Components.GhostButton onClick={() => toggle(false)}>
                            Sign Up
                        </Components.GhostButton>
                    </Components.RightOverlayPanel>
                </Components.Overlay>
            </Components.OverlayContainer>
        </Components.Container>
    );
}

export default App;
